mapper.select:
SELECT
	SCHED_NAME, ENTRY_ID, TRIGGER_NAME, TRIGGER_GROUP, JOB_NAME, JOB_GROUP, JOB_DATA, START_TIME, END_TIME, STATE, RESULT
FROM QRTZ_JOB_HISTORY
WHERE SCHED_NAME = #{schedName}
AND   ENTRY_ID   = #{entryId}

mapper.update:
UPDATE QRTZ_JOB_HISTORY
SET END_TIME = #{endTime},
	STATE = #{state},
	RESULT = #{result}
WHERE SCHED_NAME = #{schedName}
AND   ENTRY_ID   = #{entryId}

mapper.select:
SELECT
	SCHED_NAME, ENTRY_ID, TRIGGER_NAME, TRIGGER_GROUP, JOB_NAME, JOB_GROUP, JOB_DATA, START_TIME, END_TIME, STATE, RESULT
FROM QRTZ_JOB_HISTORY
WHERE JOB_NAME = #{jobName}
AND   JOB_GROUP = #{jobGroup}
ORDER BY START_TIME DESC
